import { createContext, useState } from "react";
import { toast } from "react-toastify";
import { ApiRequests } from "../services/apiRequests";

export const TechsContext = createContext({});

export const TechsProvider = ({ children }) => {
    const [techs, userTechs] = useState([])

    const submitTechsModal = async (data) => {
      // reset()
      try {
        const response = await ApiRequests.post("/sessions", data);
        localStorage.setItem("@kenzieHub:token", response.data.token);
        localStorage.setItem("@kenzieHub:id", response.data.user.id);
        setUser(response.data.user);
        toast.success("Tecnologia adicionada com sucesso.");
      } catch (error) {
        console.log(error);
        toast.error("Tecnologia não pode ser adicionada");
      }
    };


    return (
        <TechsContext.Provider value={{ techs, userTechs }}>
          {children}
        </TechsContext.Provider>
      );
}
