import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

import Logo from "../../assets/Logo.svg";
import { ApiRequests } from "../../services/apiRequests";

import { StyledSection } from "../../styles/section";
import { StyledNav } from "../../styles/nav";
import { StyledImg } from "../../styles/image";
import { StyledSpan, StyledTitle } from "../../styles/typography";
import { StyledHeader } from "../../styles/header";
import { StyledMain } from "../../styles/main";

export const Dashboard = ({ user, setUser }) => {
  let navigate = useNavigate();
  const token = localStorage.getItem("@kenzieHub:token")
  // if (!token) {
  //   navigate("/")
  // } 

  useEffect(() => {
    !token && navigate("/")
    token && navigate()
    ApiRequests.get("/profile")
      .then((response) => {
        setUser(response.data);
        console.log(response);
      })
      .catch((error) => console.log(error));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]);

  const logout = () => {
    setUser("");
    localStorage.clear();
    toast("Volte Sempre!")
  };

  return (
    <StyledSection>
      <StyledNav position="one" width="two">
        <StyledImg src={Logo} alt="" />
        <Link to={"/"}>
          <button onClick={() => logout()}>Sair</button>
        </Link>
      </StyledNav>
      <StyledHeader>
        <div>
          <StyledTitle fontSize="two" tag="h1">Olá, {user?.name}</StyledTitle>
          <StyledSpan fontSize="three" color="one">{user?.course_module}</StyledSpan>
        </div>
      </StyledHeader>
      <StyledMain>
        <StyledTitle fontSize="two" margin="three" text="one" tag="h2">Que pena! Ainda estamos em desenvolvimento :(</StyledTitle>
        <StyledSpan fontSize="two" color="three" text="one">Nossa aplicação está em desenvolvimento, em breve teremos novidades. Aguarde!</StyledSpan>
      </StyledMain>
    </StyledSection>
  );
};
