import React, { useContext, useEffect, useState } from "react";
import { BsTrash } from "react-icons/bs";

import { TechsContext } from "../../../contexts/TechsContext";

import { StyledSpan, StyledTitle } from "../../../styles/typography";

export const Technologies = ({ tech }) => {
  const { setShowEditAndDeleteModal, currentTech, setCurrentTech, deleteTechsModal } = useContext(TechsContext);
  const [mobile, setMobile] = useState(false)

  const getIdTech = (tech) => {
    setCurrentTech({ ...tech });
    setShowEditAndDeleteModal(true);
  };

  useEffect(() => { 
    if(window.innerWidth < 500 && !mobile) {
      setMobile(true);
    } else if (mobile) {
      setMobile(false);
    }
   }, [window.innerWidth])


  return (
    <li onClick={() => getIdTech(tech)}>
      <StyledTitle fontSize="four" tag="h2"> {tech.title} </StyledTitle>
      <div>
        {mobile ? (
          <StyledSpan fontSize="three" color="one"> {tech.status} </StyledSpan>
        ) : (
          <>
            <StyledSpan fontSize="three" color="one"> {tech.status} </StyledSpan>
            <button onClick={() => deleteTechsModal(currentTech.id)}><BsTrash/></button>
          </>
        )}
      </div>
    </li>
  );
};
