import { Link } from "react-router-dom";
import { Form } from "./RegisterForm/RegisterForm";
import * as yup from "yup";

import Logo from "../../assets/Logo.svg";

export const Cadastro = () => {

  const formSchema = yup.object().shape({
    name: yup
      .string()
      .trim()
      .max(18, "Deve conter no máximo 18 caracteres")
      .required("Nome obrigatório"),
    email: yup
      .string()
      .trim()
      .required("E-mail Obrigatório")
      .email("E-mail inválido"),
    password: yup
      .string()
      .trim()
      .required("Senha obrigatória"),
    //   .matches(
    //     [
    //       /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[$*&@#])(?:([0-9a-zA-Z$*&@#])(?!\1)){8,}$/,
    //     ],
    //     "A senha deve conter pelo menos 8 caracteres, sendo 1 caracter especial, 1 letra maiuscula, 1 letra minúscula e 1 número. Caracteres repetidos em sequencia não são permitidos ex: 11, aa, @@"
    //   ),
    confirmPassword: yup
      .string()
      .trim()
      .required("Confirmação de senha obrigatória")
      .oneOf([yup.ref("password"), null], "As senhas devem ser iguais"),
    bio: yup
      .string()
      .trim()
      .required("Fale um pouco sobre você")
      .min(20, "Deve conter pelo menos 20 caracteres"),
    contact: yup
      .string()
      .trim()
      .required("Insira o teu link de contato ou telefone")
      .url("Precisa ser um link válido")
      .matches("^[0-9]{2}?[0-9]{4,5}[0-9]{4}$", "telefone inválido"),
    course_module: yup
      .string()
      .required("Selecione o módulo"),
  });

  return (
    <section>
      <div>
        <img src={Logo} alt="logo" />
        <Link to={"/"}>Voltar</Link>
      </div>
      <div>
        <h2>Crie a sua conta</h2>
        <span>Rápido e grátis, vamos nessa</span>
        <Form formSchema={formSchema} />
      </div>
    </section>
  );
};
