import styled from "styled-components";

export const StyledSection = styled.section`
  background-color: var(--color-grey-4);
  width: 100vw;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  /* justify-content: center; */

  ${({ align }) => {
    if (text === "one") {
      return css`
        line-height: 25px;
        text-align: justify;
      `;
    }
  }}



  .information {
    width: 90%;
    height: fit-content;
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: var(--color-grey-3);
    gap: 20px;
  }
`;
