import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { formSchemaModal } from "./modalSchema";

import { StyledSpan, StyledTitle } from "../../../styles/typography";
import { StyledForm, StyledSelect } from "../../../styles/form";

export const TechModal = () => {
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchemaModal),
  });

  return (
    <>
      <div>
        <StyledTitle fontSize="four" tag="h3">
          Cadastrar Tecnologias
        </StyledTitle>
        <button>X</button>
      </div>

      <StyledForm>
        <div>
          <label htmlFor="title">Nome</label>
          <input
            type="text"
            id="title"
            placeholder="Digite a tecnologia"
            {...register("title")}
          />
        </div>
        <StyledSpan fontSize="two" color="two">
          {errors.title?.message}
        </StyledSpan>

        <div>
          <StyledSelect {...register("course_module")}>
            <optgroup label="Selecione o módulo">
              <option>Primeiro módulo (Introdução ao Frontend)</option>
              <option>Segundo módulo (Frontend Avançado)</option>
              <option>Terceiro módulo (Introdução ao Backend)</option>
              <option>Quarto módulo (Backend Avançado)</option>
            </optgroup>
          </StyledSelect>
        </div>
        <StyledSpan fontSize="two" color="two">
          {errors.status?.message}
        </StyledSpan>
      </StyledForm>
    </>
  );
};
