import React, {
  useContext,
  useEffect,
  useState,
  useCallback,
  useMemo,
} from "react";
import { BsTrash } from "react-icons/bs";

import { TechsContext } from "../../../contexts/TechsContext";

import { StyledSpan, StyledTitle } from "../../../styles/typography";

export const Technologies = ({ tech }) => {
  const {
    setShowEditAndDeleteModal,
    currentTech,
    setCurrentTech,
    deleteTechsModal,
  } = useContext(TechsContext);
  const [mobile, setMobile] = useState(false);

  const getIdTech = (tech) => {
    setCurrentTech({ ...tech });
    setShowEditAndDeleteModal(true);
  };

  const isMobile = useCallback(() => {
    if (window.innerWidth < 500) {
      setMobile(true);
    } else {
      setMobile(false);
    }
  }, []);

  useEffect(() => {
    window.addEventListener("resize", isMobile);
    return () => {
      window.removeEventListener("resize", isMobile);
    };
  });

  return (
    <li onClick={() => getIdTech(tech)}>
      <StyledTitle fontSize="four" tag="h2">
        {" "}
        {tech.title}{" "}
      </StyledTitle>
      {mobile ? (
        <StyledSpan fontSize="three" color="one">
          {tech.status}
        </StyledSpan>
      ) : (
        <div>
          <StyledSpan fontSize="three" color="one">
            {tech.status}
          </StyledSpan>
          <button
            onClick={(event) => {
              event.stopPropagation();
              deleteTechsModal(tech.id);
            }}
          >
            <BsTrash />
          </button>
        </div>
      )}
    </li>
  );
};
