import styled, { css } from "styled-components";

export const StyledList = styled.ul`
  width: 100%;
  padding: 15px 0;
  background-color: var(--color-grey-3);

  li {
    width: 90%;
    display: flex;
    justify-content: space-between;
    margin: 0 auto;

    &:hover {
       background-color: var(--color-grey-2);
    }

  }
`;
