import React from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { toast } from "react-toastify";

import { ApiRequests } from "../../../services/apiRequests";
import { StyledForm, StyledSelect } from "../../Login/login";

export const RegisterForm = ({ formSchema }) => {
  let navigate = useNavigate();
  // const token = localStorage.getItem("@kenzieHub:token")
  // if (token && token !== "undefined") {
  //   navigate("/dashboard")
  // } 


  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });


  const submit = (data) => {
    reset();
    ApiRequests.post("/users", data)
      .then((response) => {
        console.log(response);
        navigate("/");
        toast.success("Cadastro realizado com sucesso.", {
          autoClose: 2000,
        })
      })
      .catch((error) => {
        console.log(error)
        toast.error("Cadastro não pôde ser realizado", {
          autoClose: 2000,
        })
      });
  };


  return (
    <StyledForm onSubmit={handleSubmit(submit)}>
      <div>
        <label htmlFor="name">Nome</label>
        <input id="name" type="text" {...register("name")} />
      </div>
      <span>{errors.name?.message}</span>
      <div>
        <label htmlFor="email">E-mail</label>
        <input id="email" type="email" {...register("email")} />
      </div>
      <span>{errors.email?.message}</span>
      <div>
        <label htmlFor="password">Senha</label>
        <input id="password" type="password" {...register("password")} />
      </div>
      <span>{errors.password?.message}</span>
      <div>
        <label htmlFor="confirmPassword">Confirmar Senha</label>
        <input
          id="confirmPassword"
          type="password"
          {...register("confirmPassword")}
        />
      </div>
      <span>{errors.confirmPassword?.message}</span>
      <div>
        <label htmlFor="bio">Bio</label>
        <input id="bio" type="text" {...register("bio")} />
      </div>
      <span>{errors.bio?.message}</span>
      <div>
        <label htmlFor="contact">Contato</label>
        <input id="contact" type="text" {...register("contact")} />
      </div>
      <span>{errors.contact?.message}</span>
      <StyledSelect>
        <select {...register("course_module")}>
          <option>Selecione o módulo</option>
          <option>Primeiro módulo (Introdução ao Frontend)</option>
          <option>Segundo módulo (Frontend Avançado)</option>
          <option>Terceiro módulo (Introdução ao Backend)</option>
          <option>Quarto módulo (Backend Avançado)</option>
        </select>
      </StyledSelect>
      <span>{errors.course_module.message === "Selecione o módulo" && errors.course_module?.message}</span>
      <button type="submit">Cadastrar</button>
    </StyledForm>
  );
};
