import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

import Logo from "../../assets/Logo.svg";
import { UserContext } from "../../contexts/UserContext";
import { TechsContext } from "../../contexts/TechsContext";
import Technologies from "./Technologies/Technologies";
import { TechModal } from "./Modal/TechModal";

import { StyledSection } from "../../styles/section";
import { StyledNav } from "../../styles/nav";
import { StyledImg } from "../../styles/image";
import { StyledSpan, StyledTitle } from "../../styles/typography";
import { StyledHeader } from "../../styles/header";
import { StyledMain } from "../../styles/main";
import { StyledButton } from "../../styles/buttons";

export const Dashboard = () => {
  const { user, logout } = useContext(UserContext);
  const [showModal, setShowModal] = useState(false);

  return (
    <StyledSection>
      <StyledNav position="one" width="two">
        <StyledImg src={Logo} alt="" />
        <button onClick={() => logout()}>Sair</button>
      </StyledNav>
      <StyledHeader>
        <div>
          <StyledTitle fontSize="two" tag="h1">
            Ol√°, {user?.name}
          </StyledTitle>
          <StyledSpan fontSize="three" color="one">
            {user?.course_module}
          </StyledSpan>
        </div>
      </StyledHeader>
      <StyledMain>
        <StyledSection align="two">
          <StyledTitle fontSize="one" margin="three" text="one" tag="h2">
            {" "}
            Tecnologias{" "}
          </StyledTitle>
          <StyledButton onClick={() => setShowModal(true)}>+</StyledButton>
        </StyledSection>
        {!user?.techs.length ? (
          <StyledTitle fontSize="one" text="one" tag="h2">
            {" "}
            Nenhuma tecnologia foi cadastrada ainda!{" "}
          </StyledTitle>
        ) : (
          <ul>
            user.techs.map((tech) => <Technologies tech={tech} key={tech.id} />)

          </ul>
        )}
      </StyledMain>
      {showModal && <TechModal />}
    </StyledSection>
  );
};
