import { React } from "react";
import { Link, useNavigate } from "react-router-dom";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { toast } from "react-toastify";

import { ApiRequests } from "../../services/apiRequests";
import  Logo  from "../../assets/Logo.svg";
import { StyledSection } from "./login";
import { StyledSpan, StyledTitle } from "../../styles/typography";
import { StyledForm } from "../../styles/form";


export const Login = () => {

  let navigate = useNavigate();


  const formSchema = yup.object().shape({
    email: yup
      .string()
      .trim()
      .required("Digite o seu e-mail")
      .email("E-mail inválido"),
    password: yup
      .string()
      .trim()
      .required("Digite a sua senha"),
  })


  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });


  const submit = (data) => {
    reset();
    ApiRequests.post("/sessions", data)
    .then((response) => {
      localStorage.setItem("@kenzieHub:token", response.data.token)
      localStorage.setItem("@kenzieHub:id", response.data.user.id)
      navigate("/dashboard")
      toast.success("Login realizado com sucesso.", {
        position: "top-center",
        autoClose: 2000,
      })
    })
    .catch((error) => {
      console.log(error)
      toast.error("Login não pôde ser realizado", {
        autoClose: 2000,
      })
    });
  };


  return (
    <StyledSection>
      <StyledImg src={Logo} alt="logo" />
      <StyledForm onSubmit={handleSubmit(submit)}>
        <StyledTitle fontSize="one" margin="one" tag="h1">Login</StyledTitle>
        <div>
          <label htmlFor="email">E-mail</label>
          <input type="email" id="email" placeholder="Digite o seu e-mail"{...register("email")}/>
        </div>
        <StyledSpan fontSize="two" color="two">{errors.email?.message}</StyledSpan>
        <div>
          <label htmlFor="password">Senha</label>
          <input type="password" id="password" placeholder="Digite a sua senha" {...register("password")}/>
        </div>
        <StyledSpan fontSize="two" color="two">{errors.password?.message}</StyledSpan>
        <button>Entrar</button>
        <section className="registerBlock">
          <StyledSpan fontSize="one" color="one">Ainda não possui uma conta?</StyledSpan>
          <Link to={"/register"}> Cadastre-se </Link>
        </section>
      </StyledForm>
    </StyledSection>
  );
};
