import { createContext, useState } from "react";
import { toast } from "react-toastify";
import { ApiRequests } from "../services/apiRequests";

export const TechsContext = createContext({});

export const TechsProvider = ({ children }) => {
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditAndDeleteModal, setshowEditAndDeleteModal] = useState(false);

  const addTechsModal = async (data) => {
    // reset()
    try {
      await ApiRequests.post("/users/techs", data);
      toast.success("Tecnologia criada com sucesso.");
      setShowAddModal(false)
    } catch (error) {
      console.log(error);
      toast.error("Essa tecnologia já foi criada, é possível editá-la.");
    }
  };

  const editTechsModal = async (data, id) => {
    // reset()
    try {
      await ApiRequests.post(`/users/techs/${id}`, data);
      toast.success("Tecnologia criada com sucesso.");
      setshowEditAndDeleteModal(false)
    } catch (error) {
      console.log(error);
      toast.error("Essa tecnologia já foi criada, é possível editá-la.");
    }
  };

  const deleteTechsModal = async (data, id) => {
    // reset()
    try {
      await ApiRequests.post(`/users/techs/${id}`);
      toast.success("Tecnologia criada com sucesso.");
      setshowEditAndDeleteModal(false)
    } catch (error) {
      console.log(error);
      toast.error("Essa tecnologia já foi criada, é possível editá-la.");
    }
  };

  return (
    <TechsContext.Provider value={{ addTechsModal, editTechsModal, deleteTechsModal, showAddModal, setShowAddModal, showEditAndDeleteModal, setshowEditAndDeleteModal }}>
      {children}
    </TechsContext.Provider>
  );
};
