import styled, { css } from "styled-components";

export const StyledButton = styled.button`
  color: var(--color-white);
  background-color: var(--color-grey-3);
  padding;


  /* ${({ width }) => {
    if (width === "one") {
      return css`
        width: 23rem;
      `;
    } else if (width === "two") {
      return css`
        width: 70%;
      `;
    }
  }} */
`;
