import { createContext, useState } from "react";
import { toast } from "react-toastify";
import { ApiRequests } from "../services/apiRequests";

export const TechsContext = createContext({});

export const TechsProvider = ({ children }) => {
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditAndDeleteModal, setShowEditAndDeleteModal] = useState(false);
  const [currentTech, setCurrentTech] = useState(null)

  const getUserTechs = async () => {
    try {
      const response = await ApiRequests.get("/profile");
      setUser(response.data);
    } catch (error) {
      console.log(error);
    }
  }

  const addTechsModal = async (data) => {
    // reset()
    try {
      await ApiRequests.post("/users/techs", data);
      toast.success("Tecnologia criada com sucesso.");
      setShowAddModal(false)
    } catch (error) {
      console.log(error);
      toast.error("Essa tecnologia já foi criada, é possível editá-la.");
    }
  };

  const editTechsModal = async (data, id) => {
    // reset()
    try {
      await ApiRequests.put(`/users/techs/${id}`, data);
      toast.success("Tecnologia editada com sucesso.");
      setShowEditAndDeleteModal(false)
    } catch (error) {
      console.log(error);
      toast.error("blabla");
    }
  };

  const deleteTechsModal = async (id) => {
    // reset()
    try {
      await ApiRequests.delete(`/users/techs/${id}`);
      toast.success("Tecnologia excluída com sucesso.");
      setShowEditAndDeleteModal(false)
    } catch (error) {
      console.log(error);
      toast.error("Não foi possivel excluir");
    }
  };

  return (
    <TechsContext.Provider value={{ addTechsModal, editTechsModal, deleteTechsModal, showAddModal, setShowAddModal, showEditAndDeleteModal, setShowEditAndDeleteModal, currentTech, setCurrentTech }}>
      {children}
    </TechsContext.Provider>
  );
};
