import { useContext } from "react";
import { useForm } from "react-hook-form";

import { TechsContext } from "../../../../contexts/TechsContext";

import { StyledForm, StyledSelect } from "../../../../styles/form";
import { StyledTitle } from "../../../../styles/typography";

export const EditAndDeleteTechModal = () => {
  const { setshowEditAndDeleteModal } = useContext(TechsContext);

  const { handleSubmit, reset } = useForm();

  return (
    <>
      <div className="information">
        <StyledTitle fontSize="four" tag="h3">
          Tecnologia Detalhes
        </StyledTitle>
        <button onClick={() => setshowEditAndDeleteModal(false)}>X</button>
      </div>

      <StyledForm onSubmit={handleSubmit()}>
        <div>
          <label htmlFor="title">Nome do projeto</label>
          <input type="text" id="title" disabled />
        </div>

        <div>
        <label htmlFor="title">Nome do projeto</label>
          <StyledSelect>
            <optgroup label="Selecione o novo status">
              <option>Iniciante</option>
              <option>Intermediário</option>
              <option>Avançado</option>
            </optgroup>
          </StyledSelect>
        </div>

        <div>
          <button>Salvar Alterações</button>
          <button>Excluir</button>
        </div>
      </StyledForm>
    </>
  );
};
