import styled, { css } from "styled-components";

export const StyledSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem 0;
  min-height: 100vh;

  ${({ align }) => {
    if (align === "one") {
      return css`
        justify-content: center;
      `;
    } else if (align === "two") {
      return css`

      `;
    }
  }}

  .information {
    width: 90%;
    height: fit-content;
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: var(--color-grey-3);
    gap: 20px;

    @media (min-width: 400px) {
      width: 23rem;
    }
  }
`;
