import { useContext } from "react";
import { useForm } from "react-hook-form";

import { TechsContext } from "../../../../contexts/TechsContext";

import { StyledForm, StyledSelect } from "../../../../styles/form";
import { StyledTitle } from "../../../../styles/typography";

export const EditAndDeleteTechModal = () => {
  const { setShowEditAndDeleteModal, editTechsModal, deleteTechsModal, currentTech } = useContext(TechsContext);

  const { register, handleSubmit, reset } = useForm();

  return (
    <>
      <div className="information">
        <StyledTitle fontSize="four" tag="h3">
          Tecnologia Detalhes
        </StyledTitle>
        <button type="button" onClick={() => setShowEditAndDeleteModal(false)}>X</button>
      </div>

      <StyledForm onSubmit={handleSubmit((data) => editTechsModal(data, currentTech.id))}>
        <div>
          <label htmlFor="title">Nome do projeto</label>
          <input type="text" id="title" readOnly value={currentTech.title} disabled />
        </div>

        <div>
          <StyledSelect defaultValue={currentTech.status} {...register("status")}>
            <optgroup  label="Selecione o novo status">
              <option defaultValue="Iniciante">Iniciante</option>
              <option defaultValue="Intermediário">Intermediário</option>
              <option defaultValue="Avançado">Avançado</option>
            </optgroup>
          </StyledSelect>
        </div>

        <div>
          <button >Salvar Alterações</button>
          <button type="button" onClick={deleteTechsModal()}>Excluir</button>
        </div>tTe
      </StyledForm>
    </>
  );
};
