import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

import Logo from "../../assets/Logo.svg";
import { ApiRequests } from "../../services/apiRequests";
import { AuthContext } from "../../contexts/AuthContext";

import { StyledSection } from "../../styles/section";
import { StyledNav } from "../../styles/nav";
import { StyledImg } from "../../styles/image";
import { StyledSpan, StyledTitle } from "../../styles/typography";
import { StyledHeader } from "../../styles/header";
import { StyledMain } from "../../styles/main";

export const Dashboard = ({ user, setUser }) => {
  const navigate = useNavigate();
  const {} = useContext(AuthContext)

  const token = localStorage.getItem("@kenzieHub:token");
  useEffect(() => {
    !token && navigate("/");
    ApiRequests.get("/profile")
      .then((response) => {
        setUser(response.data);
        console.log(response);
      })
      .catch((error) => console.log(error));
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const logout = () => {
    setUser("");
    localStorage.clear();
    toast("Volte Sempre!");
    navigate("/")
  };

  return (
    <StyledSection>
      <StyledNav position="one" width="two">
        <StyledImg src={Logo} alt="" />
        <button onClick={() => logout()}>Sair</button>
      </StyledNav>
      <StyledHeader>
        <div>
          <StyledTitle fontSize="two" tag="h1">
            Olá, {user?.name}
          </StyledTitle>
          <StyledSpan fontSize="three" color="one">
            {user?.course_module}
          </StyledSpan>
        </div>
      </StyledHeader>
      <StyledMain>
        <StyledTitle fontSize="two" margin="three" text="one" tag="h2">
          Que pena! Ainda estamos em desenvolvimento :(
        </StyledTitle>
        <StyledSpan fontSize="two" color="three" text="one">
          Nossa aplicação está em desenvolvimento, em breve teremos novidades.
          Aguarde!
        </StyledSpan>
      </StyledMain>
    </StyledSection>
  );
};
