import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";

import Logo from "../../assets/Logo.svg";
import { ApiRequests } from "../../services/apiRequests";

import { StyledSection } from "../../styles/section";
import { StyledNav } from "../../styles/nav";
import { StyledImg } from "../../styles/image";
import { StyledTitle } from "../../styles/typography";
import { StyledHeader } from "../../styles/header";
import { StyledMain } from "../../styles/main";

export const Dashboard = ({ user, setUser }) => {

  useEffect(() => {
    ApiRequests.get("/profile")
      .then((response) => {
        setUser(response.data);
        console.log(response);
      })
      .catch((error) => console.log(error));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const logout = () => {
    setUser("");
    localStorage.clear();
    toast("Volte Sempre!", {
      autoClose: 2000,
      className: "toast-logout"
    })
  };

  return (
    <StyledSection>
      <StyledNav position="one">
        <StyledImg src={Logo} alt="" />
        <Link to={"/"}>
          <button onClick={() => logout()}>Sair</button>
        </Link>
      </StyledNav>
      <StyledHeader>
        <StyledTitle fontSize="two" tag="h1">Olá, {user?.name}</StyledTitle>
        <span>{user?.course_module}</span>
      </StyledHeader>
      <StyledMain>
        <StyledTitle fontSize="two" margin="three" text="one" tag="h2">Que pena! Ainda estamos em desenvolvimento :(</StyledTitle>
        <span>Nossa aplicação está em desenvolvimento, em breve teremos novidades. Aguarde!</span>
      </StyledMain>
    </StyledSection>
  );
};
